<?phprequire_once 'controller_helper.php';class User extends controller_helper {    function __construct() {        parent::__construct();        $this->load->model('user_persistance');    }    function index() {        $this->alreadyLoggedIn();        $this->form_validation->set_rules('username', 'Username', 'required');        $this->form_validation->set_rules('password', 'Password', 'required');        if ($this->form_validation->run() == FALSE) {            $this->addViewData('info', array('Please login to continue'));        } else if ($this->user_persistance->user_has_identity() == FALSE) {            $this->addViewData('error', array('Incorrect username or password'));        } else {            redirect('dashboard', 'refresh');            exit();        }        $this->loadview('login');    }    function logout() {        $this->session->sess_destroy();        redirect('user', 'refresh');    }}?>